from Bio import SeqIO


def get_shared_uniq_elements(list_1, list_2):
    shared_set = set(list_1).intersection(list_2)
    list_1_uniq = []
    for e1 in list_1:
        if e1 not in shared_set:
            list_1_uniq.append(e1)
    list_2_uniq = []
    for e2 in list_2:
        if e2 not in shared_set:
            list_2_uniq.append(e2)
    return shared_set, list_1_uniq, list_2_uniq


fa_old = '/Users/songweizhi/Desktop/SMP/genome_skimming/Maeva_Sponge_COI/X_samples_COX1.fas'
fa_new = '/Users/songweizhi/Desktop/sss/X_samples_COX1[18].fas'

seq_set_old = set()
for seq_record in SeqIO.parse(fa_old, 'fasta'):
    seq_set_old.add(seq_record.description)

seq_set_new = set()
for seq_record in SeqIO.parse(fa_new, 'fasta'):
    seq_set_new.add(seq_record.description)

print(seq_set_old)
print(seq_set_new)


shared_set, old_uniq, new_uniq = get_shared_uniq_elements(seq_set_old, seq_set_new)
print(len(shared_set), shared_set)
print(len(old_uniq), sorted(list(old_uniq)))
print(len(new_uniq), sorted(list(new_uniq)))

